class Solution:
    def minimumCost(self, m: int, n: int, hC: List[int], vC: List[int]) -> int:
        horizontal_pieces = 1
        vertical_pieces = 1
        total_cost = 0
        cuts = []
        for cost in hC:
            cuts.append((cost, 'h'))
        for cost in vC:
            cuts.append((cost, 'v'))
        cuts.sort(reverse=True)

        for cost, cut_type in cuts:
            if cut_type == 'h':
                total_cost += cost * vertical_pieces
                horizontal_pieces += 1
            else:
                total_cost += cost * horizontal_pieces
                vertical_pieces += 1
            #print(total_cost)

        return total_cost
