class Solution {
    class Pair{
        char ch;
        int cost;
        Pair(char ch, int cost){
            this.ch=ch;
            this.cost=cost;
        }
    }
    public long minimumCost(int m, int n, int[] arr, int[] arr1) {

        List<Pair>lst = new ArrayList<>();

        for(int i=0;i<arr.length;++i){

            lst.add(new Pair('H',arr[i]));
        }
        for(int j=0;j<arr1.length;++j){
            lst.add(new Pair('V',arr1[j]));
        }
        Collections.sort(lst,(a,b)->b.cost-a.cost);

        long answer=0;
        int first=1;
        int second=1;
        for(Pair ob:lst){
            if(ob.ch=='H'){
                answer+=(long)ob.cost*first;
                ++second;
            }
            else{
                answer+=(long)ob.cost*second;
                ++first;
            }
        }
         return answer;
    }

}
