using ll = long long;
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(), h.end());
        sort(v.begin(), v.end());
        ll ans = 0;
        ll H = 1, V = 1;
        while (1) {
            if (h.empty() && v.empty()) break;
            if (h.empty()) {
                ll b = v.back(); v.pop_back(); ++V;
                ans += H*b;
            } else if (v.empty()) {
                ll b = h.back(); h.pop_back(); ++H;
                ans += V * b;
            } else if (v.back() > h.back()) {
                ll b = v.back(); v.pop_back(); ++V;
                ans += H*b;
            } else {
                ll b = h.back(); h.pop_back(); ++H;
                ans += V * b;
            }
        }
        return ans;
    }
};
