class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        int[][] cost = {horizontalCut, verticalCut};
        int[] count = {1, 1};
        PriorityQueue<int[]> pq = new PriorityQueue<>(Comparator.comparingInt(asd -> -cost[asd[0]][asd[1]]));
        for (int i = 0; i < m - 1; i++)
            pq.add(new int[]{0, i});
        for (int i = 0; i < n - 1; i++)
            pq.add(new int[]{1, i});

        long ans = 0;
        while (!pq.isEmpty()) {
            int[] temp = pq.poll();
            int i = temp[0], j = temp[1];
            ans += (long) count[i ^ 1] * cost[i][j];
            count[i]++;
        }
        return ans;
    }
}
