typedef long long LL;

class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& a, vector<int>& b) {
        sort(a.begin(), a.end());
        reverse(a.begin(), a.end());

        sort(b.begin(), b.end());
        reverse(b.begin(), b.end());

        int cnta = 1, cntb = 1;

        LL res = 0;
        for (int i = 0, j = 0; i < n - 1 || j < m - 1;) {
            if (i >= n - 1) res += (LL)b[j++]*cntb;
            else if (j >= m - 1) res += (LL)a[i++]*cnta;
            else if (a[i] < b[j]) res += (LL)b[j++]*cntb, cnta++;
            else res += (LL)a[i++]*cnta, cntb++;
        }

        return res;
    }
};
