#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        ll res = 0;
        sort(h.begin(),h.end(), greater<int>());
        sort(v.begin(),v.end(), greater<int>());
        ll hzntl = 1, vert = 1;
        ll i = 0, j = 0;
        while (i < h.size() && j < v.size())
        {
            if (h[i] > v[j])
            {
                res += h[i] * vert;
                hzntl++;
                i++;
            }
            else
            {
                res += v[j] * hzntl;
                vert++;
                j++;
            }
        }

        ll total = 0;
        while (i < h.size())
            total += h[i++];
        res += total * vert;
        total = 0;
        while (j < v.size())
            total += v[j++];
        res += total * hzntl;

        return res;
    }
};
