class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(), h.end(), greater<int>());
        h.push_back(0);
        sort(v.begin(), v.end(), greater<int>());
        v.push_back(0);
        long long res = 0;
        for(int i = 0, j = 0; i < m - 1 || j < n - 1;) {
            if(h[i] > v[j]) {
                res += 1ll* h[i] * (j + 1);
                ++i;
            } else {
                res += 1ll * v[j] * (i + 1);
                ++j;
            }
        }
        return res;
    }
};
