class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        h = 1
        v = 1
        hq = []
        vq = []
        for cut in horizontalCut:
            heapq.heappush(hq,cut*(-1))
        for cut in verticalCut:
            heapq.heappush(vq,cut*(-1))

        ans = 0
        while hq or vq:
            if hq and vq:
                if hq[0] < vq[0]:
                    ans += heapq.heappop(hq)*h
                    v += 1
                else:
                    ans += heapq.heappop(vq)*v
                    h += 1
            elif hq:
                ans += heapq.heappop(hq)*h
            elif vq:
                ans += heapq.heappop(vq)*v


        return ans*(-1)
