class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& rcost, vector<int>& ccost) {
        sort(rcost.begin(), rcost.end(), greater<int>());
        sort(ccost.begin(), ccost.end(), greater<int>()); 

        typedef long long ll;
        ll res = 0;

        ll szr = 1, szc = 1;
        for (int i = 0, j = 0; i < m - 1 || j < n - 1; )
        {
            if (i == m - 1) // 没法切行了，只能切列
            {
                res = res + szr * ccost[j]; // 行数*切列的花费
                szc ++ ;  // 列数增加
                j ++ ;
            }
            else if (j == n - 1)
            {
                // 横着切，需要*竖着的个数，szc
                res = res + szc * rcost[i];
                szr ++ ;
                i ++ ;
            }
            else
            {
                if (rcost[i] > ccost[j])
                {
                    // 切行
                    res = res + szc * rcost[i];
                    szr ++ ;
                    i ++ ;
                }
                else
                {
                    res = res + szr * ccost[j]; // 行数*切列的花费
                    szc ++ ;  // 列数增加
                    j ++ ;
                }
            }
        }
        return res;
    }
};
