class Solution:
    def minimumCost(self, m: int, n: int, hc: List[int], vc: List[int]) -> int:
        cost = sorted([(x, 0) for x in hc] + [(x, 1) for x in vc])[::-1]
        mul = [1, 1]
        ans = 0
        for x, i in cost:
            ans += x * mul[i]
            mul[1-i] += 1
        return ans
