public class Solution {
    public long MinimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        List<(int, int)> ls = [];
        foreach (int h in horizontalCut) ls.Add((h, 0));
        foreach (int v in verticalCut) ls.Add((v, 1));
        ls.Sort((x, y) => y.Item1 - x.Item1);

        long res = 0;
        int ph = 1, pv = 1, last = -1;
        foreach (var v in ls) {
            int cost = v.Item1, t = v.Item2;
            res += cost * (t == 0 ? ph : pv);

            if (t == 0) pv++;
            else ph++;
            last = t;
        }

        return res;
    }
}
