class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(), h.end());
        sort(v.begin(), v.end());

        reverse(h.begin(), h.end());
        reverse(v.begin(), v.end());

        long long cost = 0;
        int h_pieces = 1, v_pieces = 1;
        int i = 0, j = 0;
        while(h_pieces < m && v_pieces < n) {
            if(h[i] > v[j]) {
                cost += v_pieces * h[i];
                i++;
                h_pieces++;
            } else {
                cost += h_pieces * v[j];
                j++;
                v_pieces++;
            }
        }

        while(h_pieces < m) {
            cost += v_pieces * h[i];
            i++;
            h_pieces++;
        }

        while(v_pieces < n) {
            cost += h_pieces * v[j];
            j++;
            v_pieces++;
        }

        return cost;
    }
};
