class Solution
{
public:
    long long minimumCost(int m, int n, vector<int> &horizontalCut, vector<int> &verticalCut)
    {
        long long ans = 0;
        long long hcnt = 0, vcnt = 0;

        sort(verticalCut.begin(), verticalCut.end(), greater<int>());
        sort(horizontalCut.begin(), horizontalCut.end(), greater<int>());

        hcnt++;
        vcnt = hcnt;

        int i = 0, j = 0;

        while (i < m - 1 && j < n - 1)
        {
            if (horizontalCut[i] > verticalCut[j])
            {
                long long val = horizontalCut[i];
                i++;
                hcnt++;
                ans += val * vcnt;
            }
            else
            {
                long long val = verticalCut[j];
                j++;
                vcnt++;
                ans += val * hcnt;
            }
        }

        long long sm = 0;
        while (i < m - 1)
        {
            sm += horizontalCut[i];
            i++;
        }
        ans += sm * vcnt;

        sm = 0;
        while (j < n - 1)
        {
            sm += verticalCut[j];
            j++;
        }
        ans += sm * hcnt;

        return ans;
    }
};
