func minimumCost(m int, n int, horizontalCut []int, verticalCut []int) int64 {
	var r int
	a, b := 1, 1
	type node struct {
		v int
		t bool
	}
	nodes := []node{}
	for _, x := range horizontalCut {
		nodes = append(nodes, node{x, false})
	}
	for _, x := range verticalCut {
		nodes = append(nodes, node{x, true})
	}
	sort.Slice(nodes, func(i, j int) bool {
		return nodes[i].v > nodes[j].v
	})
	for _, n := range nodes {
		v, t := n.v, n.t
		if t {
			a++
			r += b * v
		} else {
			b++
			r += a * v
		}
	}
	return int64(r)
}
