class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long ans=0;
        sort(h.begin(), h.end());
        sort(v.begin(), v.end());

        int i = m - 2; // m-2 because h has m-1 elements, index range is 0 to m-2
        int j = n - 2; // n-2 because v has n-1 elements, index range is 0 to n-2
        int hc = 1, vc = 1;

        while (i >= 0 && j >= 0) {
            if (h[i] > v[j]) {
                ans += h[i] * vc;
                hc++;
                i--;
            } else {
                ans += v[j] * hc;
                vc++;
                j--;
            }
        }

        while (i >= 0) {
            ans += h[i] * vc;
            i--;
        }

        while (j >= 0) {
            ans += v[j] * hc;
            j--;
        }

        return ans;
    }
};
