import heapq
class Solution:
    def minimumCost(self, m: int, n: int, hc: List[int], vc: List[int]) -> int:
        heap = [[i*(-1), "V"] for i in vc]
        for i in hc:
            heap.append([i*(-1), "H"])
        heapq.heapify(heap)
        h = v = 1
        ans = 0
        for i in range(n+m-2):
            x, y = heapq.heappop(heap)
            x *= (-1)
            if y=="H":
                ans += x*v
                h += 1
            else:
                ans += x*h
                v += 1
        return ans
