class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        vector<vector<int>>v;
        for(auto i:hc) v.push_back({i,0});
        for(auto i:vc) v.push_back({i,1});
        sort(v.begin(),v.end());
        reverse(v.begin(),v.end());
        long long int cnt0=1;
        long long int cnt1=1;
        long long int ans=0;
        for(int i=0;i<v.size();i++)
        {
            if(v[i][1]==0) 
            {
                ans+=cnt1*((long long int)v[i][0]);
                cnt0+=1;
            }
            else 
            {
                ans+=cnt0*((long long int)v[i][0]);
                cnt1+=1;
            }
        }
        return ans;
    }
};
