class Solution:
    def minimumCost(self, m: int, n: int, h: List[int], v: List[int]) -> int:
        res = 0
        h, v = sorted(h, reverse=True), sorted(v, reverse=True)
        x, y, i, j = 1, 1, 0, 0
        while i < m-1 and j < n-1:
            if h[i] > v[j]:
                res += h[i]*y
                x, i = x+1, i+1
            else:
                res += v[j]*x
                y, j = y+1, j+1
        res += y * sum(h[i:]) + x * sum(v[j:])
        return res
