class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& ah, vector<int>& av) {
        long long hm=1, vm=1;
        sort(ah.begin(), ah.end());
        reverse(ah.begin(), ah.end());
        sort(av.begin(), av.end());
        reverse(av.begin(), av.end());
        int ih=0, iv=0;
        long long ans = 0;
        while(ih<n-1 && iv<m-1){
            if(ah[ih]>av[iv]){
                ans += ah[ih]*hm;
                vm++;
                ih++;
            }
            else{
                ans += av[iv]*vm;
                hm++;
                iv++;
            }
        }
        while(ih<n-1){
            ans += ah[ih]*hm;
            ih++;
        }
        while(iv<m-1){
            ans += av[iv]*vm;
            iv++;
        }
        return ans;
    }
};
