class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(),h.end(),greater<int>());
        sort(v.begin(),v.end(),greater<int>());
        long long ans=0;
        long long i=0,j=0;
        while(i<h.size() && j<v.size()){
            if(h[i]>v[j]){
                ans+=(1ll)*h[i]*(j+1);
                i++;
            }
            else{
                ans+=(1ll)*v[j]*(i+1);
                j++;
            }
        }

        while(i<h.size()){
            ans+=(1ll)*h[i]*(j+1);
            i++;
        }
        while(j<v.size()){
            ans+=(1ll)*v[j]*(i+1);
            j++;
        }

        return ans;
    }
};
