import java.util.Arrays;

class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        // Combine all cuts into a single array of objects with type and cost
        int totalCuts = horizontalCut.length + verticalCut.length;
        int[][] cuts = new int[totalCuts][2];
        for (int i = 0; i < horizontalCut.length; i++) {
            cuts[i] = new int[] {horizontalCut[i], 0}; // 0 represents horizontal cut
        }
        for (int i = 0; i < verticalCut.length; i++) {
            cuts[horizontalCut.length + i] = new int[] {verticalCut[i], 1}; // 1 represents vertical cut
        }

        // Sort cuts by cost in descending order
        Arrays.sort(cuts, (a, b) -> b[0] - a[0]);

        // Use the greedy strategy to cut from highest cost to lowest cost
        int horizontalSections = 1;
        int verticalSections = 1;
        long totalCost = 0;

        for (int[] cut : cuts) {
            int cost = cut[0];
            int type = cut[1];

            if (type == 0) {
                // Horizontal cut
                totalCost += (long) cost * verticalSections;
                horizontalSections++;
            } else {
                // Vertical cut
                totalCost += (long) cost * horizontalSections;
                verticalSections++;
            }
        }

        return totalCost;
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        int m1 = 3, n1 = 2;
        int[] horizontalCut1 = {1, 3};
        int[] verticalCut1 = {5};
        System.out.println(sol.minimumCost(m1, n1, horizontalCut1, verticalCut1)); // Output: 13

        int m2 = 2, n2 = 2;
        int[] horizontalCut2 = {7};
        int[] verticalCut2 = {4};
        System.out.println(sol.minimumCost(m2, n2, horizontalCut2, verticalCut2)); // Output: 15
    }
}
