class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCuts, vector<int>& vCuts) {
        sort(hCuts.rbegin(), hCuts.rend());
        sort(vCuts.rbegin(), vCuts.rend());

        int i = 0, j = 0;
        int hSegments = 1, vSegments = 1;
        long long totalCost = 0;

        while (i < hCuts.size() && j < vCuts.size()) {
            if (hCuts[i] > vCuts[j]) {
                totalCost += hCuts[i] * vSegments;
                hSegments++;
                i++;
            } else {
                totalCost += vCuts[j] * hSegments;
                vSegments++;
                j++;
            }
        }
        while (i < hCuts.size()) {
            totalCost += hCuts[i] * vSegments;
            i++;
        }
        while (j < vCuts.size()) {
            totalCost += vCuts[j] * hSegments;
            j++;
        }

        return totalCost;
    }
};
