class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCuts, vector<int>& vCuts) {
        long long int hSeg = 1, vSeg = 1;
        sort(hCuts.rbegin(), hCuts.rend());
        sort(vCuts.rbegin(), vCuts.rend());


        long long int cost = 0;
        long long int i = 0, j = 0;

        for (; i < hCuts.size() && j < vCuts.size();) {
            if (hCuts[i] <= vCuts[j]) {
                cost += vCuts[j] * hSeg;
                vSeg++;
                j++;
            } else {
                cost += hCuts[i] * vSeg;
                hSeg++;
                i++;
            }
        }

        for (; j < vCuts.size(); j++) {
            cost += vCuts[j] * hSeg;
        }


        for (; i < hCuts.size(); i++) {
            cost += hCuts[i] * vSeg;
        }



        return cost;
    }
};
