object Solution {
    def minimumCost(m: Int, n: Int, horizontalCut: Array[Int], verticalCut: Array[Int]): Long = {
        val allCuts = (horizontalCut.map((_, false)) ++ verticalCut.map((_, true))).sortBy(-_._1)

        var horizontalParts = 1
        var verticalParts = 1

        var totalCost: Long = 0

        for ((cost, isVert) <- allCuts) {
            if (isVert) {
                totalCost += cost.toLong * horizontalParts
                verticalParts += 1
            } else {
                totalCost += cost.toLong * verticalParts
                horizontalParts += 1
            }
        }

        totalCost
    }
}
