class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        heap = []
        for i, n in enumerate(horizontalCut):
            heapq.heappush(heap, [-n, 0])

        for i , n in enumerate(verticalCut):
            heapq.heappush(heap, [-n, 1])

        cnt = [1, 1]
        res = 0
        while heap:
            cost, idx = heapq.heappop(heap)
            res += (-cost) * cnt[1^idx]
            cnt[idx] += 1
        return res
