class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<int> h;
        priority_queue<int> v;
        for(int i=0;i<m-1;i++){
            h.push(horizontalCut[i]);
        }
        for(int i=0;i<n-1;i++){
            v.push(verticalCut[i]);
        }
        long long int hcuts=0;
        long long int vcuts=0;
        long long int ans=0;
        while(!h.empty()&&!v.empty()){
            int htop=h.top();
            int vtop=v.top();
            if(htop>vtop){
                hcuts++;
                ans+=(1ll*htop)*(vcuts+1);
                h.pop();
            }
            else{
                vcuts++;
                ans+=(1ll*vtop)*(hcuts+1);
                v.pop();
            }
        }
        while(!h.empty()){
                hcuts++;
                ans+=(1ll*h.top())*(vcuts+1);
                h.pop();
        }
        while(!v.empty()){
                vcuts++;
                ans+=(1ll*v.top())*(hcuts+1);
                v.pop();
        }

        return ans;
    }
};
