class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        struct Cut {
            int cost;
            char type; // 'H' for horizontal, 'V' for vertical
        };

        auto compareCuts = [](const Cut &a, const Cut &b) {
            return a.cost > b.cost;
        };

        vector<Cut> cuts;

        // Add all horizontal cuts
        for (int i = 0; i < horizontalCut.size(); ++i) {
            cuts.push_back({horizontalCut[i], 'H'});
        }

        // Add all vertical cuts
        for (int i = 0; i < verticalCut.size(); ++i) {
            cuts.push_back({verticalCut[i], 'V'});
        }

        // Sort cuts in descending order of cost
        sort(cuts.begin(), cuts.end(), compareCuts);

        int horizontalPieces = 1;
        int verticalPieces = 1;
        long long totalCost = 0;

        for (const auto &cut : cuts) {
            if (cut.type == 'H') {
                totalCost += cut.cost * verticalPieces;
                horizontalPieces++;
            } else {
                totalCost += cut.cost * horizontalPieces;
                verticalPieces++;
            }
        }

        return totalCost;
    }
};
