class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
        vector<pair<int,int>> c;
        for(auto it : a){
            c.push_back({it,0});
        }
        for(auto it : b){
            c.push_back({it,1});
        }
        sort(c.begin(),c.end(),greater<pair<int,int>>());
        long long h = 0, v = 0;
        long long ans = 0;
        for(auto it : c){
            if(it.second == 0){
                ans += (long long)(it.first * (long long)(v+1));
                h++;
            }
            else{
                ans += (long long)(it.first * (long long)(h+1));
                v++;
            }
        }
        return ans;
    }
};
