class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        costs = [(a, 'H') for a in horizontalCut] + [(a, 'V') for a in verticalCut]

        costs.sort(key = lambda x: -x[0])

        res = 0
        h,v = 1,1
        for c,t in costs:
            if t == 'H':
                res += h * c
                v += 1
            else:
                res += v * c
                h += 1

        return res
