class Solution:
    def minimumCost(self, m: int, n: int, H: List[int], V: List[int]) -> int:
        H.sort()
        V.sort()

        ans = 0
        c = 0
        r = 0

        while H or V:
            if H and V:
                if H[-1] > V[-1]:
                    ans += H.pop()*(c+1)
                    r += 1
                else:
                    ans += V.pop()*(r+1)
                    c += 1
            elif H:
                ans += H.pop()*(c+1)
                r += 1
            else:
                ans += V.pop()*(r+1)
                c += 1



        return ans
