class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        long ans = 0;
        int v = 1, h = 1;
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);
        int i = m - 2, j = n - 2;
        while (i >= 0 || j >= 0) {
            if (j < 0) {
                ans += horizontalCut[i] * h;
                i--;
                v++;
            } else if (i < 0) {
                ans += verticalCut[j--] * v;
                h++;
            } else if (horizontalCut[i] > verticalCut[j]) {
                ans += horizontalCut[i] * h;
                i--;
                v++;
            } else {
                ans += verticalCut[j--] * v;
                h++;
            }
        }
        return ans;
    }
}
