class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hzCut, vector<int>& vlCut) {
         sort(vlCut.begin(), vlCut.end(), greater<int>());
         sort(hzCut.begin(), hzCut.end(), greater<int>());
    long long Cost=0;
    long long v = 0, h = 0;
    long long hzCuts = 1, vlCuts = 1;

    while (h < hzCut.size() && v < vlCut.size()) {
        if (hzCut[h] > vlCut[v]) {
            Cost += hzCut[h] * vlCuts;
              hzCuts++;
            h++;

        } else {
           Cost += vlCut[v] * hzCuts;
              vlCuts++;
            v++;

        }
    }

    // Adding remained Hz cuts
    while (h < hzCut.size()) {
       Cost += hzCut[h] * vlCuts;
        h++;
    }

    //adding remained cuts
    while (v < vlCut.size()) {
       Cost += vlCut[v] * hzCuts;
        v++;
    }

    return Cost;
    }
};
