#define ll long long

class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& h, vector<int>& v) {
        sort(h.begin(),h.end(),greater<int>());
        sort(v.begin(),v.end(),greater<int>());
        n--,m--;
        ll i=0,j=0,ans=0;
        while(i<n || j<m){
            if(i<n and j<m){
                if(h[i]>v[j]){
                    ans+=(ll)(j+1)*h[i];
                    i++;
                }else{
                    ans+=(ll)(i+1)*v[j];
                    j++;
                }
            }else if(i<n){
                ans+=(ll)(j+1)*h[i];
                i++;
            }else{
                ans+=(ll)(i+1)*v[j];
                j++;
            }
        }
        return ans;
    }
};
