class Solution {
public:
    long long minimumCost(int M, int N, vector<int>& X, vector<int>& Y) {
        int v_parts=1,h_parts=1;
        long long cost=0;
        sort(X.begin(),X.end(),greater<int>());
        sort(Y.begin(),Y.end(),greater<int>());
        int i=0,j=0;
        while(i<M-1 && j<N-1)
        {
            if(X[i]>=Y[j])
            {
                cost+=(X[i]*h_parts);
                i++;
                v_parts++;
            }
            else
            {
                cost+=(Y[j]*v_parts);
                j++;
                h_parts++;
            }
        }
        while(i<M-1)
        {
            cost+=(X[i]*h_parts);
            i++;
            v_parts++;
        }
        while(j<N-1)
        {
            cost+=(Y[j]*v_parts);
            j++;
            h_parts++;
        }
        return cost;
    }
};
