class Solution {
public:
    #define ll long long
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
          ll ans=0;

        m--;
        n--;

        sort(hor.begin(),hor.end());

        sort(ver.begin(),ver.end());

        reverse(hor.begin(),hor.end());

        reverse(ver.begin(),ver.end());

        ll h=1,v=1;

        int i=0,j=0;
        while(i<m&&j<n)
        {
            if(hor[i]>ver[j])
            {
                ans+=(ll)hor[i]*(ll)v;
                h++;
                i++;
            }
            else
            {
                ans+=(ll)ver[j]*(ll)h;
                v++;
                j++;
            }
        }
        ll par=0;

        while(i<m)
        {
            par+=(ll)hor[i++];
        }
        ans+=(ll)par*(ll)v;

        par=0;
        while(j<n)
        {
            par+=(ll)ver[j++];
        }

        ans+=(ll)par*(ll)h;

        return ans;
    }
};
