class Solution {
public:
   long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.rbegin(), h.rend());sort(v.rbegin(), v.rend());
        long long cn=0;int hs =1,se=1;int hi=0,vi=0; 
        while (hi<h.size() &&vi<v.size()){if(h[hi]>=v[vi]) {cn+=(long long)h[hi]*se;
                hs++;hi++;
            }else{cn += (long long)v[vi]*hs;se++;
                vi++;
            }
        } while (hi<h.size()) {cn+=(long long)h[hi]*se;hi++;
            hs++;
        }while (vi<v.size()) {cn+=(long long)v[vi]*hs;vi++;se++;
        }        return cn;
    }
};
