class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        priority_queue<pair<int,char>> pq;
        for(int i=0;i<h.size();i++){
            pq.push({h[i],'h'});
        }
        for(int i=0;i<v.size();i++){
            pq.push({v[i],'v'});
        }

        int hc=1,vc=1;
        long long ans=0;
        while(!pq.empty()){
           int val=pq.top().first;
           char c=pq.top().second;
           pq.pop();
           if(c=='v'){
                ans += hc*val;
                vc++;
           }else{
                ans += vc*val;
                hc++;
           }
        }

        return ans;
    }
};
