class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        output = 0
        x_count = 1
        y_count = 1
        heap = []
        for i, num in enumerate(horizontalCut):
            heappush(heap, [-num, i, 0])
        for i, num in enumerate(verticalCut):
            heappush(heap, [-num, i, 1])
        while heap:
            cost, j, dirr = heappop(heap)
            if dirr == 0:
                output -= cost * y_count
                x_count += 1
            else:
                output -= cost * x_count
                y_count += 1
        return output
