class Solution {
public:


    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
            sort(a.rbegin(),a.rend());
            sort(b.rbegin(),b.rend());
            n=a.size();
            m=b.size();
        long long ans =0 ,i=0,j=0;
        while(i<n && j<m){
            if(a[i]>b[j]){
                ans += (a[i]*(j+1));
                i++;
            }else {
                ans += (b[j]*(i+1));
                j++;
            }
        }
        while(i<n)ans+=(a[i++]*(j+1));
        while(j<m)ans+=(b[j++]*(i+1));

        return ans;

    }
};
