class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        priority_queue<long long> h, v;
        for(auto &i: hc) h.push(i);
        for(auto &i: vc) v.push(i);

        long long hm = 1, vm = 1;
        long long ans = 0;
        while(h.size() || v.size()) {
            if(!v.size()) {
                auto htop = h.top() * hm;
                h.pop();
                ans += htop;
            }

            else if (!h.size()) {
                auto vtop = v.top() * vm;
                v.pop();
                ans += vtop;
            }

            else {
                auto htop = h.top() * hm;
                auto vtop = v.top() * vm;

                if(h.top() > v.top()) {
                    ans += htop;
                    h.pop();
                    vm++;
                }
                else {
                    ans += vtop;
                    v.pop();
                    hm++;
                }
            }
        }

        return ans;
    }
};
