class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& A, vector<int>& B) {
        sort(A.begin(),A.end());
        sort(B.begin(),B.end());
        --n;
        --m;
        for (int i=0,j=n-1;i<j;++i,--j) swap(A[i],A[j]);
        for (int i=0,j=m-1;i<j;++i,--j) swap(B[i],B[j]);
        long long ans=0;
        int i=0,j=0;
        for (;i<n && j<m;)
        {
            if (A[i]>B[j])
            {
                ans+=1LL*A[i]*(j+1);
                ++i;
            }
            else
            {
                ans+=1LL*B[j]*(i+1);
                ++j;
            }
        }
        while (i<n) ans+=1LL*A[i]*(m+1),++i;
        while (j<m) ans+=1LL*B[j]*(n+1),++j;
        return ans;
    }
};
