#define sqrt(x) sqrtl(x)
#define ll long long

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {


        ll ans = 0;
        int HP = 1, VP = 1, hi = 0, veci = 0;

        sort(all(v), greater<int>());
        sort(all(h), greater<int>());

        while (hi < h.size() && veci < v.size()) {
            if (h[hi] >= v[veci]) 
            {
                ans += (ll)(h[hi]) * 1LL * VP;
                HP++;
                hi++;
            } else {
                ans += (ll)(v[veci]) * 1LL * HP;
                VP++;
                veci++;
            }
        }

        while (hi < h.size()) 
        {
            ans += (ll)(h[hi]) * 1LL * VP;
            hi++;
        }

        while (veci < v.size()) 
        {
            ans += (ll)(v[veci]) * 1LL * HP;
            veci++;
        }

        return ans;
    }
};
