class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        int hs=h.size(),vs=v.size();
        long long ans=0;
        sort(h.begin(),h.end());
        sort(v.begin(),v.end());
        int t1=1,t2=1;
        int i=hs-1,j=vs-1;
        while(i>=0 and j>=0){
            if (h[i]>=v[j]){
                ans+=(long long)h[i]*t1;
                t2++;
                i--;
            }
            else {
                ans+=(long long)v[j]*t2;
                t1++;
                j--;
            }
        }
        while(i>=0){
            ans+=(long long)h[i]*t1;
            i--;
        }
        while(j>=0){
            ans+=(long long)v[j]*t2;
            j--;
        }
        return ans;
    }
};
