class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);

        long totalCost = 0;
        int hP = horizontalCut.length - 1;
        int vP = verticalCut.length - 1;

        int gPie = 1;
        int vPie = 1;

        while (hP >= 0 || vP >= 0) {
            if (hP >= 0 && (vP < 0 || horizontalCut[hP] >= verticalCut[vP])) {
                totalCost += horizontalCut[hP] * vPie;
                gPie++;
                hP--;
            } else {
                totalCost += verticalCut[vP] * gPie;
                vPie++;
                vP--;
            }
        }

        return totalCost;
    }
}
