class Solution {
    public long minimumCost(int m, int n, int[] Hcost, int[] Vcost) {
        Arrays.sort(Vcost);
        Arrays.sort(Hcost);
        int[] cost_x = new int[Hcost.length];
        int[] cost_y = new int[Vcost.length];
        for(int i = 0;i<Hcost.length;i++)
        {
            cost_x[i] = Hcost[Hcost.length-1-i];
        }
        for(int i = 0;i<Vcost.length;i++)
        {
            cost_y[i] = Vcost[Vcost.length-1-i];
        }
        long minCost = 0;
        long hp = 1;
        long vp = 1;
        int hi = 0;
        int vi = 0;
        while(hi < cost_y.length && vi < cost_x.length){
            if(cost_y[hi] > cost_x[vi]){
                minCost = minCost + vp*cost_y[hi];
                hp++;
                hi++;
            } else {
                minCost = minCost + hp*cost_x[vi];
                vi++;
                vp++;
            }
        }
        while(hi < cost_y.length){
            minCost = minCost + vp*cost_y[hi];
            hi++;
        }
        while(vi< cost_x.length){
             minCost = minCost + hp*cost_x[vi];
            vi++;
        }

        return minCost;
    }
}
