class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,int>> vec;
        for(auto val:horizontalCut)
            vec.push_back({val,1});
        for(auto val:verticalCut)
            vec.push_back({val,2});
        sort(vec.rbegin(),vec.rend());

        long ans = 0;
        int h=1, v=1;

        for(auto val:vec){
            if(val.second == 1){
                ans += val.first*v;
                h++;
            }
            else{
                ans += val.first*h;
                v++;
            }
        }

        return ans;
    }
};
