class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h_cuts, vector<int>& v_cuts) {
        std::sort(h_cuts.rbegin(), h_cuts.rend());
        std::sort(v_cuts.rbegin(), v_cuts.rend());

        long long total_cost = 0;  
        int hCuts = 1, vCuts = 1;  
        int i = 0, j = 0;

        while (i < h_cuts.size() && j < v_cuts.size()) {
            if (h_cuts[i] > v_cuts[j]) {
                total_cost += static_cast<long long>(h_cuts[i++]) * vCuts;
                hCuts++;
            } else {
                total_cost += static_cast<long long>(v_cuts[j++]) * hCuts;
                vCuts++;
            }
        }

        while (i < h_cuts.size()) {
            total_cost += static_cast<long long>(h_cuts[i++]) * vCuts;
        }
        while (j < v_cuts.size()) {
            total_cost += static_cast<long long>(v_cuts[j++]) * hCuts;
        }

        return total_cost;
        }
};
