class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.rbegin(), hc.rend());
        sort(vc.rbegin(), vc.rend());
        long long h = 1, v = 1, i = 0, j = 0, ans = 0;
        while (i < m - 1 && j < n - 1) {
            // cout << hc[i] << ", v[j]: " << vc[j] << endl;
            // cout << "hs: " << h << ", vs: " << v << endl;
            if (hc[i] > vc[j]) {
                ans += hc[i] * v;
                h++;
                i++;
            } else {
                ans += vc[j] * h;
                v++;
                j++;
            }
        }
        long long rem = 0;
        while (i < m - 1)
            rem += hc[i++];
        ans += rem * v;
        rem = 0;
        while (j < n - 1)
            rem += vc[j++];
        ans += rem * h;
        return ans;
    }
};
