class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        horizontal_cuts = 1
        vertical_cuts = 1
        horizontalCut.sort()
        verticalCut.sort()
        ret = 0
        while horizontalCut and verticalCut:
            if horizontalCut[-1] > verticalCut[-1]:
                x = horizontalCut.pop()
                ret += x * vertical_cuts
                horizontal_cuts += 1
            else:
                x = verticalCut.pop()
                ret += x * horizontal_cuts
                vertical_cuts += 1
        if horizontalCut:
            ret += sum(horizontalCut) * vertical_cuts
        if verticalCut:
            ret += sum(verticalCut) * horizontal_cuts
        return ret
