class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<pair<int, char>> pq;
        for (int cost : horizontalCut) {
            pq.push({cost, 'H'});
        }
        for (int cost : verticalCut) {
            pq.push({cost, 'V'});
        }

        long long hp = 1, vp = 1; 
        long long ans = 0; 

        while (!pq.empty()) {
            auto [cost, type] = pq.top();
            pq.pop();

            if (type == 'H') {
                ans += cost * vp;
                hp++;
            } else {
                ans += cost * hp;
                vp++;
            }
        }

        return ans;
    }
};
